import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { useSpring, animated } from 'react-spring';

const useStyles = makeStyles((theme) => ({
  banner: {
    backgroundColor: '#0077C2',
    color: '#FFFFFF',
    padding: theme.spacing(4),
    textAlign: 'center',
  },
  title: {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    marginBottom: theme.spacing(2),
  },
  description: {
    fontSize: '1.5rem',
    marginBottom: theme.spacing(4),
  },
  button: {
    backgroundColor: '#00BFFF',
    color: '#FFFFFF',
    padding: theme.spacing(2),
    borderRadius: theme.shape.borderRadius,
    fontSize: '1.2rem',
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    '&:hover': {
      backgroundColor: '#00619a',
    },
  },
}));

const Banner = () => {
  const classes = useStyles();

  const animationProps = useSpring({
    from: { opacity: 0, transform: 'translateY(50px)' },
    to: { opacity: 1, transform: 'translateY(0px)' },
    config: { duration: 1000 },
  });

  return (
    <animated.section className={classes.banner} style={animationProps}>
      <div>
        <h1 className={classes.title}>Seja bem-vindo ao nosso blog!</h1>
        <p className={classes.description}>
          Descubra conteúdos incríveis e mantenha-se atualizado sobre as últimas tendências.
        </p>
        <button className={classes.button}>Explorar</button>
      </div>
    </animated.section>
  );
};

export default Banner;
