import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Typography, Link, Grid, TextField, Button } from '@material-ui/core';
import { GitHub, LinkedIn, Twitter } from '@material-ui/icons';
import { useSpring, animated } from 'react-spring';

const useStyles = makeStyles((theme) => ({
  footer: {
    padding: '2rem 0',
    backgroundColor: '#F5F5F5',
    textAlign: 'center',
  },
  section: {
    marginBottom: '2rem',
  },
  socialIcons: {
    marginTop: '1rem',
  },
  textField: {
    marginBottom: '1rem',
  },
  subscribeButton: {
    marginLeft: '0.5rem',
  },
}));

const Footer = () => {
  const classes = useStyles();
  const animationProps = useSpring({
    opacity: 1,
    from: { opacity: 0 },
    config: { duration: 1000 },
  });

  return (
    <animated.footer className={classes.footer} style={animationProps}>
      <animated.div className={classes.section}>
        <Typography variant="h6" component="h6">
          Links
        </Typography>
        <nav>
          <Link href="/about" color="textPrimary">
            Sobre
          </Link>
          <Link href="/contact" color="textPrimary">
            Contato
          </Link>
          <Link href="/privacy" color="textPrimary">
            Política de Privacidade
          </Link>
          <Link href="/terms" color="textPrimary">
            Termos de Uso
          </Link>
        </nav>
      </animated.div>
      <animated.div className={classes.section}>
        <Typography variant="h6" component="h6">
          Redes Sociais
        </Typography>
        <Grid container justify="center" className={classes.socialIcons}>
          <Grid item>
            <Link href="https://github.com/brunopaulon" target="_blank" rel="noopener" color="textPrimary">
              <GitHub />
            </Link>
          </Grid>
          <Grid item>
            <Link href="https://www.linkedin.com/in/brunopaulon/" target="_blank" rel="noopener" color="textPrimary">
              <LinkedIn />
            </Link>
          </Grid>
          <Grid item>
            <Link href="https://twitter.com/brunopaulon_" target="_blank" rel="noopener" color="textPrimary">
              <Twitter />
            </Link>
          </Grid>
        </Grid>
      </animated.div>
      <animated.div className={classes.section}>
        <Typography variant="h6" component="h6">
          Assine nosso boletim informativo
        </Typography>
        <Typography variant="body2" component="p">
          Receba as últimas atualizações do blog diretamente em sua caixa de entrada.
        </Typography>
        <form noValidate autoComplete="off">
          <TextField
            id="email"
            label="Endereço de e-mail"
            variant="outlined"
            size="small"
            className={classes.textField}
          />
          <Button variant="contained" color="primary" className={classes.subscribeButton}>
            Assinar
          </Button>
        </form>
      </animated.div>
    </animated.footer>
  );
};

export default Footer;
