import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Typography, Grid, Card, CardMedia, CardContent } from '@material-ui/core';
import { useSpring, animated } from 'react-spring';

const useStyles = makeStyles((theme) => ({
  section: {
    padding: '4rem 0',
    backgroundColor: '#F5F5F5', // --bg-100
  },
  sectionTitle: {
    marginBottom: '3rem',
    textAlign: 'center',
    color: '#005B99', // --primary-100
  },
  card: {
    display: 'flex',
    flexWrap: 'wrap',
    height: '100%',
    borderRadius: '10px',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease',
    '&:hover': {
      transform: 'scale(1.05)',
    },
    backgroundColor: '#FFFFFF',
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9 aspect ratio
  },
  content: {
    flex: '1 0 auto',
    padding: '1rem',
    overflow: 'auto',
  },
  titleCard: {
    display: 'flex',
    overflow: 'h',
    fontSize: '1.5rem',
    fontWeight: 'bold',
    marginBottom: '1rem',
    color: '#333333', // --text-100
  },
  summaryCard: {
    fontSize: '1rem',
    color: '#5c5c5c', // --text-200
  },
}));

const ContentSections = () => {
  const classes = useStyles();
  const animationProps = useSpring({
    opacity: 1,
    from: { opacity: 0 },
    config: { duration: 1000 },
  });

  // Dados simulados das seções de conteúdo
  const contentSections = [
    {
      id: 1,
      title: 'Notícias',
      articles: [
        {
          id: 1,
          title: 'Descoberta revolucionária na otimização de performance',
          summary:
            'Uma nova técnica de otimização de performance foi descoberta, trazendo melhorias significativas no tempo de carregamento e experiência do usuário.',
          image: 'https://via.placeholder.com/300x200',
        },
        {
          id: 2,
          title: 'Novo framework JavaScript lançado',
          summary:
            'Um novo framework JavaScript foi lançado recentemente, prometendo simplificar o desenvolvimento front-end e melhorar a produtividade dos desenvolvedores.',
          image: 'https://via.placeholder.com/300x200',
        },
      ],
    },
    {
      id: 2,
      title: 'Dicas',
      articles: [
        {
          id: 3,
          title: '5 dicas para melhorar a performance do seu site',
          summary:
            'Confira essas 5 dicas essenciais para otimizar a performance do seu site, desde a compressão de imagens até o uso eficiente de recursos de cache.',
          image: 'https://via.placeholder.com/300x200',
        },
        {
          id: 4,
          title: 'Como criar animações incríveis com CSS',
          summary:
            'Aprenda a criar animações impressionantes utilizando CSS. Descubra técnicas avançadas e dicas práticas para tornar seu site mais dinâmico e interativo.',
          image: 'https://via.placeholder.com/300x200',
        },
      ],
    },
    // Adicione mais seções de conteúdo conforme necessário
  ];

  return (
    <animated.section className={classes.section} style={animationProps}>
      {contentSections.map((section) => (
        <div key={section.id}>
          <Typography variant="h2" component="h2" className={classes.sectionTitle}>
            {section.title}
          </Typography>
          <Grid container spacing={3}>
            {section.articles.map((article) => (
              <Grid item xs={12} sm={6} md={4} key={article.id}>
                <animated.div style={animationProps}>
                  <Card className={classes.card}>
                    <CardMedia
                      className={classes.media}
                      image={article.image}
                      title={article.title}
                    />
                    <CardContent className={classes.content}>
                      <Typography variant="h3" component="h3" className={classes.titleCard}>
                        {article.title}
                      </Typography>
                      <Typography variant="body1" component="p" className={classes.summaryCard}>
                        {article.summary}
                      </Typography>
                    </CardContent>
                  </Card>
                </animated.div>
              </Grid>
            ))}
          </Grid>
        </div>
      ))}
    </animated.section>
  );
};

export default ContentSections;
